mapper.update:
update t_auth_session
set session_key = #{sessionKey,jdbcType=VARCHAR},
  user_id = #{userId,jdbcType=BIGINT},
  create_time = #{createTime,jdbcType=TIMESTAMP},
  active_time = #{activeTime,jdbcType=BIGINT}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select id, session_key, user_id, create_time, active_time
from t_auth_session
where 1=1

    and session_key = #{sessionKey,jdbcType=VARCHAR}


    and user_id = #{userId,jdbcType=BIGINT}

select id, session_key, user_id, create_time, active_time
from t_auth_session
where session_key = #{sessionKey,jdbcType=VARCHAR}
